export default {

  async init() {
    await storeValue('ster_out_error', '');
    await qOpenRuns.run();
  },

	sql: {
    str(v) {
      if (v === undefined || v === null) return "NULL";
      return "'" + String(v).replace(/'/g, "''") + "'";
    },
    ts(d) {
      if (!d) return "NULL";
      // format without the ISO 'T'
      const s = moment(d).format("YYYY-MM-DD HH:mm:ss");
      return "'" + s + "'";
    },
    num(v) {
      const n = Number(v);
      return Number.isFinite(n) ? String(n) : "NULL";
    },
  },

  selectedRun() {
    const r = tblOpenRuns.selectedRow;
    return r && Object.keys(r).length ? r : null;
  },

  ensureRunSelected() {
    const r = this.selectedRun();
    if (!r) {
      txtErrOut.setText("Select a run in the table first.");
      return null;
    }
    return r;
  },

  async onRunSelected() {
    txtErrOut.setText("");
    const r = this.selectedRun();
    if (r) {
      storeValue("selected_ster_run", r);
      if (!inpOperatorOut.text) inpOperatorOut.setValue(appsmith.user.email);
    }
  },

  async refresh() {
    txtErrOut.setText("");
		inpGoodCount.setValue("");
		inpDestroyedCount.setValue("");
		dpEndTime.setValue(null)
    return qOpenRuns.run();
  },

  async completeRun() {
		txtErrOut.setText("");
		const errs = this.validate();
		if (errs.length) {
			txtErrOut.setText(errs.join(" "));
			return;
		}
		await qCompleteRun.run();
		await this.refresh();
	},

	validate() {
		const errs = [];
		const run = this.selectedRun();
		if (!run) {
			errs.push("Select an open run.");
			return errs;
		}

		const planned = Number(run.planned_count);
		const good = (inpGoodCount.text === "" || inpGoodCount.text === null || inpGoodCount.text === undefined)
			? 0 : Number(inpGoodCount.text);
		const bad = (inpDestroyedCount.text === "" || inpDestroyedCount.text === null || inpDestroyedCount.text === undefined)
			? 0 : Number(inpDestroyedCount.text);

		if (!Number.isFinite(good) || good < 0) errs.push("Good count must be >= 0.");
		if (!Number.isFinite(bad)  || bad  < 0) errs.push("Destroyed count must be >= 0.");

		if (Number.isFinite(good) && Number.isFinite(bad) && (good + bad) <= 0) {
			errs.push("Good + Destroyed must be > 0.");
		}

		if (Number.isFinite(planned)) {
			if (!Number.isFinite(good) || !Number.isFinite(bad)) {
				// already covered
			} else if ((good + bad) !== planned) {
				errs.push(`Good (${good}) + Destroyed (${bad}) must equal Planned Count (${planned}).`);
			}
		}

		// Operator required
		if (!inpOperatorOut.text || String(inpOperatorOut.text).trim() === "") {
			errs.push("Operator is required.");
		}

		return errs;
	},


};

