export default {
  async init() {
    await storeValue('ster_in_error', '');
    await Promise.allSettled([qItems.run(), qRecipes.run()]);
  },

  validate() {
    const errs = [];
    if (!selProcessType.selectedOptionValue) errs.push("Select a process type.");
    if (!selPlannedItem.selectedOptionValue) errs.push("Select a planned item.");
    if (!selPlannedRecipe.selectedOptionValue) errs.push("Select a planned recipe.");

    const unit = Number(inpPlannedUnitSize.text);
    const cnt  = Number(inpPlannedCount.text);
    if (!Number.isFinite(unit) || unit <= 0) errs.push("Planned unit size must be > 0.");
    if (!Number.isFinite(cnt) || cnt <= 0) errs.push("Planned count must be > 0.");

    return errs;
  },

  sql: {
    str(v) {
      if (v === undefined || v === null) return "NULL";
      return "'" + String(v).replace(/'/g, "''") + "'";
    },
    ts(d) {
      if (!d) return "NULL";
      // format without the ISO 'T'
      const s = moment(d).format("YYYY-MM-DD HH:mm:ss");
      return "'" + s + "'";
    },
    num(v) {
      const n = Number(v);
      return Number.isFinite(n) ? String(n) : "NULL";
    },
  },

	async createRun() {
    await storeValue('ster_in_error', '');
    const errs = this.validate();
    if (errs.length) {
      await storeValue('ster_in_error', errs.join(' '));
      return;
    }

		let createdRun;
    try {
      createdRun = await qCreateRun.run();
      // refresh downstream view
    } catch (e) {
      await storeValue('ster_in_error', (e && e.message) ? e.message : String(e));
			return;
    }
		await storeValue('ster_in_error','Successfully created ' + createdRun[0].steri_run_id);
  },

  async refresh() {
    await this.init();
  },
};

