		if (!Number.isFinite(good) || good < 0) errs.push("Good count must be >= 0.");
		if (!Number.isFinite(bad)  || bad  < 0) errs.push("Destroyed count must be >= 0.");

		if (Number.isFinite(good) && Number.isFinite(bad) && (good + bad) <= 0) {
			errs.push("Good + Destroyed must be > 0.");
		}

