    (SELECT COALESCE(array_agg(btbl."label_inoc_prod" ORDER BY btbl."nocopk"), '{}'::text[]) FROM "public"."_m2m_print_queue_products_product_id" j JOIN "public"."vc_products" btbl ON btbl."nocopk" = j."products_id" WHERE j."print_queue_id" = comp."nocopk") AS "label_inoc_prod_from_product_id"
  FROM "public"."v_print_queue" base
)
SELECT comp.*,
  ('PRINT-' || to_char((comp."nc_created_at")::timestamp, 'YYMMDD') || '-' || "right"((comp."airtable_id")::text, (4)::int)) AS "print_id",
